import React, {useState} from "react";
import './checkout.css'
import {FormControl, InputLabel, MenuItem, Select} from "@mui/material";

const tables = [
    {id: 1, name: 'Tavolina 1'},
    {id: 2, name: 'Tavolina 2'},
    {id: 3, name: 'Tavolina 3'},
    {id: 4, name: 'Tavolina 4'},
    {id: 5, name: 'Tavolina 5'}
]

const Tables = props => {
    const {tables, set} = props
    const [selected, setSelected] = useState({id: 1, name: 'Tavolina 1'});

    const handleChange = event => {
        console.log("theEvent", event)
        setSelected(event.target.value);
    }

    return (
        <FormControl>
            <InputLabel htmlFor="table-tag">Zgjedhni tavolinen ne te cilen jeni ulur</InputLabel>
            <Select
                value={selected}
                onChange={(event) => handleChange(event)}
                inputProps={{
                    name: "table",
                    id: "table-tag"
                }}
            >
                {tables.map((table, index) => {
                    return <MenuItem value={table}>{table}</MenuItem>;
                })}
            </Select>
        </FormControl>
    );
}
const ProductRow = props => {
    const {product} = props
    return (
        <div className={'product-row'}>
            <div className={'product-row-name'}>{product.title}</div>
            <div className={'product-row-price'}>{product.price}</div>
        </div>
    )
}

const CheckoutView = props => {
    const {items} = props
    return (
        <div className = 'checkout-root'>
            <div className={'checkout-top'}>
                {items.map(item => <ProductRow product={item}/>)}
            </div>

            <div className={'checkout-bottom'}>
                <Tables tables={tables}/>
            </div>
        </div>
    )
}
export default CheckoutView
