import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import CartTable from './CartTable';
import ClearIcon from '@mui/icons-material/Clear';
const CartDialog = props => {
    const {open, onClose, cart} = props
    const [tab, setTab] = useState('cartTable')


        return (
                <Dialog open={open} onClose={() => onClose()}>
                    <DialogTitle><div className='market'><p>Shporta</p><Button onClick={() => onClose()}><ClearIcon/></Button></div></DialogTitle>
                    <DialogContent>
                    tab === 'cartTable' ? <CartTable items={cart}/> : <CheckoutView/>
                    </DialogContent>
                    <h4>Total: {cart.map(next => next.price* next.count).reduce((current, next) => current + next, 0)}</h4>
                </Dialog>
        
        );
    
 }
 export default CartDialog;
