import React, {useState} from "react";
import './checkout.css'
import {
    Avatar, Button, Dialog, DialogActions, DialogContent,
    DialogTitle, InputLabel, ListItem, Select, TextField
} from "@material-ui/core";



const Tables = props => {
    const tables = [
        {id: 0, name: 'None'},
        {id: 1, name: 'Tavolina 1'},
        {id: 2, name: 'Tavolina 2'},
        {id: 3, name: 'Tavolina 3'},
        {id: 4, name: 'Tavolina 4'},
        {id: 5, name: 'Tavolina 5'}
    ]
    const [table, setTable] = useState(0);

    const handleChange = event => {
         console.log("theEvent", event.target)
        setTable(event.target.value);
    }

    return (
            <FormControl sx={{ minWidth: 300}}>
                <InputLabel id="tavolina-label">Zgjedhni tavolinen qe jeni ulur</InputLabel>
                <Select
                    labelId="tavolina-label"
                    //id="demo-simple-select"
                    value={table}
                    onChange={handleChange}
                    label="Zgjedhni tavolinen qe jeni ulur"
                >
                    {tables.map(table => {
                        if (table.id === 0) {
                            return <ListItem key={table.id} value={table.id} name={table.name}><em>{table.name}</em></ListItem>
                        }
                        return <ListItem key={table.id} value={table.id} name={table.name}>{table.name}</ListItem>
                    })}
                </Select>
                <Select
                    displayEmpty
                    id='label'
                    name='avatar'
                    value= {table}
                    variant={'outlined'}

                >
                    {tables.map((avatar, i) => {
                        return <ListItem value = {avatar.id} name = {avatar.name} key={i}>
                            {avatar.name}
                        </ListItem>
                    })}

                </Select>

            </FormControl>
    );
}
const ProductRow = props => {
    const {product} = props
    return (
        <div className={'product-row'}>
            <div className={'product-row-name'}>{product.title}</div>
            <div className={'product-row-price'}>{(product.price * product.count).toFixed(2)}</div>
        </div>
    )
}

const CheckoutView = props => {
    const {items} = props
    return (
        <div className='checkout-root'>
            <div className={'checkout-top'}>
                {items.map(item => <ProductRow product={item}/>)}
            </div>
            <div className={'display'}>
                <Tables/>
            </div>

        </div>
    )
}
export default CheckoutView
