import React, {useState} from "react";
import './checkout.css'
import {FormControl, InputLabel, MenuItem, Select} from "@mui/material";



const Tables = props => {
    const tables = [
        {id: 0, name: 'None'},
        {id: 1, name: 'Tavolina 1'},
      /*  {id: 2, name: 'Tavolina 2'},
        {id: 3, name: 'Tavolina 3'},
        {id: 4, name: 'Tavolina 4'},
        {id: 5, name: 'Tavolina 5'}*/
    ]
    const [table, setTable] = useState(0);

    const handleChange = event => {
         console.log("theEvent", event.target)
        setTable(event.target.value);
    }

    return (
            <FormControl sx={{ minWidth: 300, minHeight: 500, backgroundColor: 'red'}}>
                <InputLabel id="tavolina-label">Zgjedhni tavolinen qe jeni ulur</InputLabel>
                <Select
                    //labelId="tavolina-label"
                    //id="tavolinat"
                    value={table}
                    onChange={(event) => handleChange(event)}
                    autoWidth={false}
                    label="Zgjedhni tavolinen qe jeni ulur"
                    style={{display: 'block'}}

                >
                    {tables.map(table => {
                        if (table.id === 0) {
                            return <MenuItem key={table.id} value={table.id}><em>{table.name}</em></MenuItem>
                        }
                        return <MenuItem key={table.id} value={table.id}>{table.name}</MenuItem>
                    })}
                </Select>
            </FormControl>
    );
}
const ProductRow = props => {
    const {product} = props
    return (
        <div className={'product-row'}>
            <div className={'product-row-name'}>{product.title}</div>
            <div className={'product-row-price'}>{product.price}</div>
        </div>
    )
}

const CheckoutView = props => {
    const {items} = props
    return (
        <div className='checkout-root'>
            <div className={'checkout-top'}>
                {items.map(item => <ProductRow product={item}/>)}
            </div>
            <div className={'display'}>
                <Tables/>
            </div>

        </div>
    )
}
export default CheckoutView
