import React, { useState } from 'react';
import Menu from './Menu';
import Categories from './Categories';
import items from './data';
import logo from './logo.svg';
import Button from '@mui/material/Button';
import LocalMallIcon from '@mui/icons-material/LocalMall';

const allCategories = ['all', ...new Set(items.map((item) => item.category))];

function App() {
  const [menuItems, setMenuItems] = useState(items);
  const [categories, setCategories] = useState(allCategories);
  const [cart, addToCart] = useState([])
  const [isDialogOpen, setIsDialogOpen] =  useState(false)

  const filterItems = (category) => {
    if (category === 'all') {
      setMenuItems(items);
      return;
    }
    const newItems = items.filter((item) => item.category === category);
    setMenuItems(newItems);
  };

  const addItemToCart = (item) => {
    addToCart([...cart, item])
  }

  return (
    <main>
      <section className="menu section">
        <div className="title">
        <img src={logo}></img><h2>Menu</h2>
          <div className="underline"></div>
        </div>
        <div className='market'>
          <Categories categories={categories} filterItems={filterItems} />
          <Button onClick={() => setIsDialogOpen(true)}><LocalMallIcon/></Button>
        </div>
        <Categories categories={categories} filterItems={filterItems}><h1>Vedati</h1></Categories>
        <Menu items={menuItems} addToCart={(item) => addItemToCart(item)}/>
      </section>
      <section>
        {JSON.stringify(cart)}
      </section>
    </main>
  );
}

export default App;
