import React, {useState} from "react";
import './checkout.css'
import {FormControl, InputLabel, MenuItem, Select} from "@mui/material";

const tables = [
    {id: 1, name: 'Tavolina 1'},
    {id: 2, name: 'Tavolina 2'},
    {id: 3, name: 'Tavolina 3'},
    {id: 4, name: 'Tavolina 4'},
    {id: 5, name: 'Tavolina 5'}
]

const Tables = props => {
    const {tables, setTables} = props
    const [table, setTable] = useState('');

    const handleChange = event => {
        // console.log("theEvent", event)
        setTable(event.target.value);
    }

    return (
        <FormControl sx={{ m: 1, minWidth: 80 }}>
            <InputLabel id="tavolina-label">Zgjedhni tavolinen qe jeni ulur</InputLabel>
            <Select
                labelId="tavolina-label"
                id="tavolinat"
                value={table}
                onChange={handleChange}
                autoWidth
                label="Zgjedhni tavolinen qe jeni ulur"
            >
                <MenuItem value="">
                    <em>None</em>
                </MenuItem>
                <MenuItem value={1}>Tavolina 1</MenuItem>
                <MenuItem value={2}>Tavolina 2</MenuItem>
                <MenuItem value={3}>Tavolina 3</MenuItem>
            </Select>
        </FormControl>
    );
}
const ProductRow = props => {
    const {product} = props
    return (
        <div className={'product-row'}>
            <div className={'product-row-name'}>{product.title}</div>
            <div className={'product-row-price'}>{product.price}</div>
        </div>
    )
}

const CheckoutView = props => {
    const {items} = props
    return (
        <div className = 'checkout-root'>
            <div className={'checkout-top'}>
                {items.map(item => <ProductRow product={item}/>)}
            </div>

            {/*<div className={'checkout-bottom'}>*/}
            {/*   */}
            {/*</div>*/}
            <Tables className="tables" tables={tables}/>
        </div>
    )
}
export default CheckoutView
