import React, {useState} from 'react';
import Menu from './Menu';
import Categories from './Categories';
import items from './data';
import logo from './logo.svg';
import Button from '@mui/material/Button';
import LocalMallIcon from '@mui/icons-material/LocalMall';

import CartDialog from './CartDialog';

const allCategories = ['all', ...new Set(items.map((item) => item.category))];

function App() {
    const [menuItems, setMenuItems] = useState(items);
    const [categories, setCategories] = useState(allCategories);
    const [cart, addToCart] = useState([])
    const [isDialogOpen, setIsDialogOpen] = useState(false)

    const filterItems = (category) => {
        if (category === 'all') {
            setMenuItems(items);
            return;
        }
        const newItems = items.filter((item) => item.category === category);
        setMenuItems(newItems);
    };

    const addItemToCart = (item, operation) => {
        const found = cart.find(next => next.id === item.id)
        if(operation === "INCREASE"){
            const items = !!found ? cart.map((next) => next.id === found.id ? {...next, count: next.count + 1} : next)
                : [...cart, {...item, count: 1}]
            addToCart(items)
        }
        else {
            if(found) {
                const items =  cart.map((next) => next.id === found.id ? {...next, count: next.count - 1} : next)
                addToCart(items)
            }
        }





    }


    return (
        <main>
            <section className="menu section">
                <div className="title">
                    <img src={logo}></img><h2>Menu</h2>
                    <div className="underline"></div>
                </div>
                <div className='market'>
                    <Categories categories={categories} filterItems={filterItems}/>
                    <Button onClick={() => setIsDialogOpen(true)}><LocalMallIcon/><h3>{cart.reduce((acc, next) => acc + next.count, 0)}</h3></Button>
                </div>
                <Menu items={menuItems} addToCart={(item, operation) => addItemToCart(item, operation)}/>
            </section>
            <section>
                {/*{JSON.stringify(cart)}*/}
            </section>
            <CartDialog open={isDialogOpen} onClose={() => setIsDialogOpen(false)} cart={cart}/>
        </main>
    );
}

export default App;
